package week1Day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int value= -9;
		if(value>=0) {
			System.out.println("Number" + ' ' + value + ' ' + "is positive");
		}
		else {
			System.out.println("Number" + ' ' + value + ' ' + "is negative");
		}
		
	}

}
